// MenuAdmin.js
import React, { useState } from 'react';
import { Link, Route, Switch } from 'react-router-dom';
import Dashboard from './Dashboard';
import MissionsList from './MissionsList';
import CreateMission from './CreateMission';
import LeaveRequestList from './LeaveRequestList';
import StaffList from './StaffList';
import CreateStaff from './CreateStaff';
import Logout from './Logout';
import logoImage from '../logo.png';

const MenuAdmin = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className={`sidebar ${isSidebarOpen ? '' : 'close'}`}>
      <div className="sidebar close">
        <div href="" className="logo-box">
          <img src={logoImage} alt="logo-EMS" />
          <div className="logo-name">EMS</div>
        </div>

        <ul className="sidebar-list">
          {/* ... your existing menu items ... */}
        </ul>
      </div>

      <section className="home">
        <div className="toggle-sidebar" onClick={toggleSidebar}>
          <i className={`bx ${isSidebarOpen ? 'bx-menu' : 'bx-chevron-right'}`}></i>
          <div className="text">Toggle</div>
        </div>

        {/* Route Section */}
        <Switch>
          <Route path="/admin/dashboard" component={Dashboard} />
          <Route path="/admin/missions-list" component={MissionsList} />
          <Route path="/admin/create-mission" component={CreateMission} />
          <Route path="/admin/leave-request" component={LeaveRequestList} />
          <Route path="/admin/staff-list" component={StaffList} />
          <Route path="/admin/create-staff" component={CreateStaff} />
          <Route path="/auth/logout" component={Logout} />
        </Switch>
      </section>
    </div>
  );
};

export default MenuAdmin;
