// CreatePassword.jsx
import React, { useState } from 'react';
import { useParams, useHistory, useNavigate } from 'react-router-dom';

const CreatePassword = () => {
  const { email } = useParams();
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate passwords
    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    // Add logic to send the new password to the server for confirmation
    const response = await sendPasswordToServer(email, password);

    if (response.status === 200) {
      // Password created successfully, redirect to login page or another appropriate page
      navigate.push('/auth/login');
    } else {
      setError('Error creating password. Please try again.');
    }
  };

  const sendPasswordToServer = async (email, password) => {
    try {
      const response = await fetch(`http://localhost:5000/api/create-password/${email}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }),
      });

      return response;
    } catch (error) {
      console.error('Error sending password to server:', error);
      return { status: 500 };
    }
  };

  return (
    <div>
      <h2>Create Your Password</h2>
      <form onSubmit={handleSubmit}>
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <br />

        <label>Confirm Password:</label>
        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
        <br />

        {error && <p style={{ color: 'red' }}>{error}</p>}

        <button type="submit">Create Password</button>
      </form>
    </div>
  );
};

export default CreatePassword;
