import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import MenuStaff from '../../components/MenuStaff';
import '../../auth.css';

const StaffLeaveRequestList = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [filteredLeaveRequests, setFilteredLeaveRequests] = useState([]);
  const [statusFilter, setStatusFilter] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');
  const [authToken, setAuthToken] = useState(localStorage.getItem('authToken')); // Declare authToken

  useEffect(() => {
    // Fetch staff's leave requests
    const fetchStaffLeaveRequests = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/staff/leave-requests', {
          method: 'GET',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            Authorization: `Bearer ${authToken}`,
          },
        });
        if (response.ok) {
          const leaveRequestsData = await response.json();
          setLeaveRequests(leaveRequestsData);
          setFilteredLeaveRequests(leaveRequestsData);
        } else {
          console.error('Error fetching staff leave requests:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching staff leave requests:', error.message);
      }
    };

    fetchStaffLeaveRequests();
  }, [authToken]);

  
  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <>
      <MenuStaff />
      <div className="main-container">
        <div className="missions-list">
          <h1>Missions List</h1>
          <div className='form-flex'>
            <div className="upcoming-missions">
              <h2>Upcoming Missions</h2>
              <ul>
                {upcomingMissions.map((mission) => (
                  <li key={mission._id}>
                    {mission.title} - {mission.description} ({formatDate(mission.startDate)})
                  </li>
                ))}
              </ul>
            </div>

            <div className="past-missions">
              <h2>Past Missions</h2>
              <ul>
                {pastMissions.map((mission) => (
                  <li key={mission._id}>
                    {mission.title} - {mission.description} ({formatDate(mission.endDate)})
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};


export default StaffMissionsList;
