import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MenuAdmin from '../components/MenuAdmin';
import jwt from 'jsonwebtoken';

const CreateMission = () => {
  const navigate = useNavigate();

  const [missionForm, setMissionForm] = useState({
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    staffId: ''
  });

  const [staffList, setStaffList] = useState([]);

  useEffect(() => {
    const fetchStaffList = async () => {
      try {
        const authToken = localStorage.getItem('authToken');

        const response = await fetch('/api/admin/staff-list', {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        if (!response.ok) {
          throw new Error('Error fetching staff list');
        }

        const data = await response.json();
        setStaffList(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchStaffList();
  }, []);

  const handleFormChange = (e) => {
    setMissionForm({
      ...missionForm,
      [e.target.name]: e.target.value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const authToken = localStorage.getItem('authToken');

      // Call the backend API to create a mission
      const response = await fetch('/api/admin/create-mission', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${authToken}`,
        },
        body: JSON.stringify(missionForm),
      });

      if (!response.ok) {
        throw new Error('Error creating mission');
      }

      // Redirect to the missions page after successful creation
      navigate('/missions');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <>
    <Header />
    <MenuAdmin/>
    <div className="main-container">
        <h1>Create Mission</h1>
        <form onSubmit={handleFormSubmit}>
          <label>Title:</label>
          <input type="text" name="title" value={missionForm.title} onChange={handleFormChange} required />

          <label>Description:</label>
          <textarea name="description" value={missionForm.description} onChange={handleFormChange} required />

          <label>Start Date:</label>
          <input type="date" name="startDate" value={missionForm.startDate} onChange={handleFormChange} required />

          <label>End Date:</label>
          <input type="date" name="endDate" value={missionForm.endDate} onChange={handleFormChange} required />

          <label>Staff:</label>
          <select name="staffId" value={missionForm.staffId} onChange={handleFormChange} required>
            <option value="" disabled>Select Staff</option>
            {staffList.map((staff) => (
              <option key={staff._id} value={staff._id}>
                {staff.firstName} {staff.lastName}
              </option>
            ))}
          </select>

          <button type="submit">Create Mission</button>
        </form>
      </div>
      <Footer />
    </>
  );
};

export default CreateMission;
