import React, { useState } from 'react';
import '../menu.css';
import { Link } from 'react-router-dom';

const MenuAdmin = ({ role }) => {
  // États locaux pour gérer l'ouverture des sous-menus
  const [isLeaveRequestOpen, setLeaveRequestOpen] = useState(false);
  const [isMissionsOpen, setMissionsOpen] = useState(false);
  const [isStaffListOpen, setStaffListOpen] = useState(false);

  // Fonction pour ouvrir le sous-menu au clic
  const openSubMenu = (menu) => {
    setLeaveRequestOpen(menu === 'leaveRequest');
    setMissionsOpen(menu === 'missions');
    setStaffListOpen(menu === 'staffList');
  };

  return (
    <div className="menu-admin">
      <div className="dropdown">
        {/* <button className="dropbtn">Menu</button> */}
        <div className="dropdown-content">
          <Link to={`/admin/dashboard`}>Dashboard</Link>
          <div className="sub-menu">
            <span onClick={() => openSubMenu('missions')}>
              Missions
            </span>
            <div className={isMissionsOpen ? 'sub-menu-open' : 'sub-menu-closed'}>
              <Link to={`/admin/missions-list`}>Missions List</Link>
              <Link to={`/admin/create-mission`}>Create Mission</Link>
            </div>
          </div>

          <div className="sub-menu">
            <span onClick={() => openSubMenu('leaveRequest')}>
              Leave Request
            </span>
            <div className={isLeaveRequestOpen ? 'sub-menu-open' : 'sub-menu-closed'}>
              <Link to={`/admin/leave-request`}>Leave Request List</Link>
            </div>
          </div>

          <div className="sub-menu">
            <span onClick={() => openSubMenu('staffList')}>
              Staff List
            </span>
            <div className={isStaffListOpen ? 'sub-menu-open' : 'sub-menu-closed'}>
              <Link to={`/admin/staff-list`}>Staff List</Link>
              <Link to={`/admin/create-staff`}>Create Staff</Link>
            </div>
          </div>

          <Link to="/auth/login">Logout</Link>
        </div>
      </div>
    </div>
  );
};

export default MenuAdmin;
