import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Menu from './components/Menu';
import AdminRoutes from './routes/AdminRoutes';
import StaffRoutes from './routes/StaffRoutes';
import Login from './pages/Login';
import Signup from './pages/Signup';

const PrivateRoute = ({ element: Element, ...rest }) => {
  const authToken = localStorage.getItem('authToken');

  return authToken ? (
    <Route {...rest} element={<Element />} />
  ) : (
    <Navigate to="/login" />
  );
};

const App = () => {
  return (
    <>
      <Header />
      <Router>
        <Routes>
          {/* Public routes */}
          <Route path="login" element={<Login />} />
          <Route path="signup" element={<Signup />} />

          {/* Private routes for staff */}
          <PrivateRoute path="/staff/*" element={<StaffRoutes />} />

          {/* Private routes for admin */}
          <PrivateRoute path="/admin/*" element={<AdminRoutes />} />

          {/* Private routes for admin and staff */}
          <PrivateRoute
            path="/dashboard"
            element={<div>Replace this with your dashboard content</div>}
          />
        </Routes>
      </Router>
      <Footer />
    </>
  );
};

export default App;
