import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import MenuStaff from '../../components/MenuStaff';
import '../../auth.css';

const StaffMissionsList = () => {
  const [upcomingMissions, setUpcomingMissions] = useState([]);
  const [pastMissions, setPastMissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    const fetchStaffMissions = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/staff/missions', {
          method: 'GET',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            Authorization: `Bearer ${authToken}`,
          },
        });

        if (response.ok) {
          const missionsData = await response.json();
          const currentDate = new Date();

          const upcoming = missionsData.filter((mission) => new Date(mission.startDate) > currentDate);
          const past = missionsData.filter((mission) => new Date(mission.endDate) < currentDate);

          setUpcomingMissions(upcoming);
          setPastMissions(past);
        } else {
          setError('Error fetching staff missions');
        }
      } catch (error) {
        console.error('Error fetching staff missions:', error);
        setError('Internal server error');
      } finally {
        setLoading(false);
      }
    };

    fetchStaffMissions();
  }, []);
  
  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US');
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <>
      <MenuStaff />
      <div className="main-container">
        <div className="missions-list">
          <h1>Missions List</h1>
          <div className='form-flex'>
            <div className="upcoming-missions">
              <h2>Upcoming Missions</h2>
              <ul>
                {upcomingMissions.map((mission) => (
                  <li key={mission._id}>
                    {mission.title} - {mission.description} ({formatDate(mission.startDate)})
                  </li>
                ))}
              </ul>
            </div>

            <div className="past-missions">
              <h2>Past Missions</h2>
              <ul>
                {pastMissions.map((mission) => (
                  <li key={mission._id}>
                    {mission.title} - {mission.description} ({formatDate(mission.endDate)})
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};


export default StaffMissionsList;
