import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const CreatePasswordPage = () => {
  const { email } = useParams();
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Add your password validation and creation logic here

    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    // Add logic to send the new password to the server for confirmation
    // You might want to use an API call to send the data to your server

    // Example API call using fetch
    fetch('http://localhost:/api/reset-password', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email,
        password,
      }),
    })
      .then((response) => {
        if (response.ok) {
          // Password reset successfully, you can redirect the user or show a success message
        } else {
          // Handle error, show error message, etc.
        }
      })
      .catch((error) => {
        console.error('Error resetting password:', error);
        // Handle error, show error message, etc.
      });
  };

  return (
    <div>
      <h1>Create Password Page</h1>
      <p>Email: {email}</p>
      <form onSubmit={handleSubmit}>
        <label>
          Password:
          <input type="password" value={password} onChange={handlePasswordChange} />
        </label>
        <br />
        <label>
          Confirm Password:
          <input type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
        </label>
        <br />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Reset Password</button>
      </form>
    </div>
  );
};

export default CreatePasswordPage;
