import React, { useState } from 'react';
import '../menu.css';
import { Link } from 'react-router-dom';

const MenuAdmin = ({ role }) => {
  // États locaux pour gérer l'ouverture des sous-menus
  const [isLeaveRequestOpen, setLeaveRequestOpen] = useState(false);

  // Fonction pour basculer l'état des sous-menus
  const toggleSubMenu = (menu) => {
    if (menu === 'leaveRequest') {
      setLeaveRequestOpen(!isLeaveRequestOpen);
    }
    // Ajoutez d'autres états pour d'autres sous-menus si nécessaire
  };

  return (
    <div className="menu-admin">
      <ul>
        <li><Link to={`/admin/dashboard`}>Dashboard</Link></li>
        <li><Link to={`/admin/missions`}>Missions</Link></li>
        <li>
          {/* Utilisation de onClick pour gérer l'ouverture du sous-menu */}
          <span onClick={() => toggleSubMenu('leaveRequest')}>
            Leave Request
          </span>
          {/* Sous-menu avec une classe conditionnelle pour l'affichage */}
          <ul className={isLeaveRequestOpen ? 'sub-menu-open' : 'sub-menu-closed'}>
            <li><Link to={`/admin/leave-request/pending`}>Pending Requests</Link></li>
            <li><Link to={`/admin/leave-request/approved`}>Approved Requests</Link></li>
            <li><Link to={`/admin/leave-request/rejected`}>Rejected Requests</Link></li>
          </ul>
        </li>
        <li><Link to={`/admin/staff-list`}>Staff List</Link></li>
        <li><Link to="/auth/login">Logout</Link></li>
      </ul>
    </div>
  );
};

export default MenuAdmin;
