import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import MenuAdmin from '../../components/MenuAdmin';
import '../../menu.css';
import '../../styles.css';

const AdminDashboard = () => {
  const [totalStaff, setTotalStaff] = useState(0);
  const [newRegistrations, setNewRegistrations] = useState(0);
  const [totalMissions, setTotalMissions] = useState(0);
  const [staffOnLeave, setStaffOnLeave] = useState(0);

  useEffect(() => {
    // Fetch data for Admin Dashboard
    const fetchAdminDashboardData = async () => {
      try {
        // Fetch total staff count
        const totalStaffResponse = await fetch('http://localhost:5000/api/admin/total-staff');
        const totalStaffData = await totalStaffResponse.json();
        setTotalStaff(totalStaffData.count);

        // Fetch new registrations count for the current month
        const newRegistrationsResponse = await fetch('http://localhost:5000/api/admin/new-registrations');
        const newRegistrationsData = await newRegistrationsResponse.json();
        setNewRegistrations(newRegistrationsData.count);

        // Fetch total missions count for the current month
        const totalMissionsResponse = await fetch('http://localhost:5000/api/admin/total-missions');
        const totalMissionsData = await totalMissionsResponse.json();
        setTotalMissions(totalMissionsData.count);

        // Fetch total staff on leave for the current month
        const staffOnLeaveResponse = await fetch('http://localhost:5000/api/admin/staff-on-leave');
        const staffOnLeaveData = await staffOnLeaveResponse.json();
        setStaffOnLeave(staffOnLeaveData.count);
      } catch (error) {
        console.error('Error fetching Admin Dashboard data:', error);
      }
    };

    fetchAdminDashboardData();
  }, []);

  return (
    <>
      <MenuAdmin />
      <div className="main-container">
        <h1>Admin Dashboard</h1>
        <div className='form-flex'>
        {/* Total Staff */}
        <div className="dashboard-section">
          <h2>Total Staff</h2>
          <p>{totalStaff}</p>
        </div>
        </div>
        <div className='form-flex'>
        {/* Total Missions (Current Month) */}
        <div className="dashboard-section">
          <h2>Total Missions (Current Month)</h2>
          <p>{totalMissions}</p>
        </div>
        </div>
        <div className='form-flex'>
        {/* Staff on Leave (Current Month) */}
        <div className="dashboard-section">
          <h2>Staff on Leave (Current Month)</h2>
          <p>{staffOnLeave}</p>
        </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default AdminDashboard;
