import React, { useState, useEffect } from 'react';
import { getLeaveRequests, approveLeaveRequest, rejectLeaveRequest,sendApprovalEmail, sendRejectionEmail } from '../../../backend/controllers/adminController.js';
import Header from '../components/Header';
import MenuAdmin from '../components/MenuAdmin';
import Footer from '../components/Footer';

const LeaveRequestList = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [filter, setFilter] = useState('all'); // Default filter is set to 'all'

  useEffect(() => {
    // Fetch leave requests based on the selected filter
    fetchLeaveRequests(filter);
  }, [filter]);

  const fetchLeaveRequests = async (statusFilter) => {
    try {
      const response = await getLeaveRequests(statusFilter);
      setLeaveRequests(response.data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleApprove = async (leaveRequestId) => {
    try {
      await approveLeaveRequest(leaveRequestId);
      // Send approval email
      sendApprovalEmail(leaveRequestId);
      // Refetch leave requests after approval
      fetchLeaveRequests(filter);
    } catch (error) {
      console.error('Error approving leave request:', error);
    }
  };

  const handleReject = async (leaveRequestId) => {
    try {
      await rejectLeaveRequest(leaveRequestId);
      // Send rejection email
      sendRejectionEmail(leaveRequestId);
      // Refetch leave requests after rejection
      fetchLeaveRequests(filter);
    } catch (error) {
      console.error('Error rejecting leave request:', error);
    }
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  return (
    <>
    <Header />
    <MenuAdmin />

    <div className="main-container">
      <h1>Leave Requests</h1>
      <div>
        <label>Filter:</label>
        <select value={filter} onChange={handleFilterChange}>
          <option value="all">All</option>
          <option value="pending">Pending</option>
          <option value="approved">Approved</option>
          <option value="rejected">Rejected</option>
        </select>
      </div>
      <ul>
        {leaveRequests.map((request) => (
          <li key={request._id}>
            {request.staffName} - {request.startDate} to {request.endDate}
            Status: {request.status}
            {request.status === 'pending' && (
              <>
                <button onClick={() => handleApprove(request._id)}>Approve</button>
                <button onClick={() => handleReject(request._id)}>Reject</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
    <Footer />
    </>
  );
};

export default LeaveRequestList;
