// ModifyStaffForm.jsx
import React, { useState, useEffect } from 'react';

const ModifyStaffForm = ({ staffId }) => {
  const [staffDetails, setStaffDetails] = useState({});

  useEffect(() => {
    // Fetch staff details from the backend
    const fetchStaffDetails = async () => {
      try {
        const authToken = localStorage.getItem('authToken');

        const response = await fetch(`http://localhost:5000/api/admin/staff/${staffId}`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        if (!response.ok) {
          throw new Error('Error fetching staff details');
        }

        const data = await response.json();
        setStaffDetails(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchStaffDetails();
  }, [staffId]);

  // Add form modification logic here

  return (
    <div>
      <h1>Modify Staff</h1>
      {/* Add your modification form here */}
    </div>
  );
};

export default ModifyStaffForm;
