import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Menu from './components/Menu';
import AdminRoutes from './routes/AdminRoutes';
import StaffRoutes from './routes/StaffRoutes';
import Login from './pages/Login';
import Signup from './pages/Signup';

const PrivateRoute = ({ element: Element, ...rest }) => {
  const authToken = localStorage.getItem('authToken');

  return (
    <Route
      {...rest}
      element={authToken ? <Element /> : <Navigate to="/login" />}
    />
  );
};

const App = () => {
  return (
    <>
    <Header />
    <Router>
      <Routes>
        <Route path='/' element={<PrivateRoute roles={["staff"]} />}>
        <PrivateRoute path="/staff/*" element={<StaffRoutes />} />
        </Route>
        
        <Route path='/' element={<PrivateRoute roles={["admin"]} />}>
        <Route path="/admin/*" element={<AdminRoutes />} />
        </Route>
        
        <Route path='/' element={<PrivateRoute roles={["admin", "staff"]} />}>
        <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
        </Route>

      </Routes>
    </Router>
    <Footer />
    </>
  );
};

export default App;
